import fs from "fs";
import path from "path";
import classNames from "classnames";
import { useRef } from "react";
import { InputCheckboxComponent } from "./types";

export const InputCheckbox: InputCheckboxComponent = ({ id, checked = false, disabled, onChange }) => {
  const { current: inputId } = useRef(`RampInputCheckbox-${id}`)

  const updateTransaction = (approved: boolean) => {
    const transactions = require('./mock-data.json');
    const index = transactions.findIndex((t: any) => t.id === id);
    if (index !== -1) {
      transactions[index].approved = approved;
      fs.writeFileSync(path.join(__dirname, 'transactions.json'), JSON.stringify(transactions));
    }
  };

  return (
    <div className="RampInputCheckbox--container" data-testid={inputId}>
      <label
        className={classNames("RampInputCheckbox--label", {
          "RampInputCheckbox--label-checked": checked,
          "RampInputCheckbox--label-disabled": disabled,
        })}
      />
      <input
        id={inputId}
        type="checkbox"
        className="RampInputCheckbox--input"
        checked={checked}
        disabled={disabled}
        onChange={() => {onChange(!checked); updateTransaction(!checked)}
      }
      />
    </div>
  )
}
